package 연산자;

public class 비교연산자 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		// 산술연산자(사칙연산자, + - * / % )
		int x = 20;
		int y = 10;
		System.out.println(x == y); 
		System.out.println(x != y); 
		System.out.println(x > y);  
		System.out.println(x <= y);  
	}

}
