package 반복문;

public class For1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for (int i = 0; i < 3; i++) {
			System.out.println(i);
		}
		for (int i = 0; i < 100; i++) {
			System.out.println("내가 반복2");
		}
		for (int i = 0; i < 10; i++) {
			System.out.println("내가 반복3 "+ (i+1) +"번째 실행 중");
		}
	}

}
